<?phpini_set('display_errors', true);error_reporting(E_ALL | E_STRICT);// Email de notificationif(isset($_POST['EXTRADATA']) and !empty($_POST['EXTRADATA'])) {    $sendTo = $_POST['EXTRADATA'];} else {    $sendTo = 'caroline.kowalski@dalenys.com';}$message = '';// Paramsforeach ( $_POST as $parameter => $value ) {    $message .= $parameter.' = '.$value."\n\r";}// Send emailmail($sendTo, 'billOrder summary', $message);